    __secret: number[];
    __N: number;
    __guesses: number;
    __mxGuesses: number;

    constructor(secret: number[]) {
        this.__secret = secret
        this.__N = secret.length
        for (var i = 1; i < this.__N; ++i) {
            this.__secret[i] += this.__secret[i - 1];
        }
        this.__guesses = 0;
        this.__mxGuesses = 20;
    };

    _getSub(a: number, b: number): number {
        var assert = require('assert');
        assert(0 <= a && a < this.__N);
        assert(0 <= b && b < this.__N);
        assert(a <= b);
        return (a != 0) ? (this.__secret[b] - this.__secret[a - 1]) : this.__secret[b];
    };

    compareSub(l: number, r: number, x: number, y: number): number {
        this.__guesses += 1;
        var a = this._getSub(l, r), b = this._getSub(x, y);
        return (a > b) ? 1 : (a < b) ? -1 : 0;
    };

    _okGuesses(): boolean {
        return this.__guesses <= this.__mxGuesses;
    };

    length(): number {
        return this.__N;
    };
}"
