  "typescriptCustomType" : "class ArrayReader {
    __secret: number[];
    __N: number;
    __guesses: number;
    __mxGuesses: number;

    constructor(secret: number[]) {
        this.__secret = secret
        this.__N = secret.length
        this.__guesses = 0;
        this.__mxGuesses = 2 * this.__N;
    };

    query(a: number, b: number, c: number, d: number): number {
        var assert = require('assert');
        assert(0 <= a && a < this.__N);
        assert(0 <= b && b < this.__N);
        assert(0 <= c && c < this.__N);
        assert(0 <= d && d < this.__N);
        assert(a < b && b < c && c < d);
        this.__guesses += 1;
        var arr = [ this.__secret[a], this.__secret[b], this.__secret[c], this.__secret[d] ];
        arr.sort((a,b)=>a-b); 
        if(arr[0] == arr[3]) return 4;
        if(arr[0] == arr[1] && arr[2]== arr[3]) return 0;
        return 2;
    };

    _okGuesses(): boolean {
        return this.__guesses <= this.__mxGuesses;
    };

    length(): number {
        return this.__N;
    };
}
