    #elements;
    constructor(elements: number[]) {
        this.#elements = elements;
    }

    at(index: number): number {
        if (index < 0 || index >= this.size()) {
            throw new Error("index");
        }
        if (this.#elements.length <= 10) {
            return this.#elements[index];
        }
        let beg = -1;
        let end = Math.floor(this.#elements.length / 2);
        let mid;
        while (end - beg > 1) {
            mid = Math.floor((beg + end) / 2);
            if (this.#elements[mid] <= index) {
                beg = mid;
            } else {
                end = mid;
            }
        }
        return this.#elements[Math.floor(this.#elements.length / 2) + end];
    }

    size(): number {
        if (this.#elements.length <= 10) {
            return this.#elements.length;
        }
        return this.#elements[Math.floor(this.#elements.length / 2) - 1];
    }
}"
