"typescriptCustomType" : "class HtmlParser {
    maps: Map<string, number>;
    urls: string[];
    a: Map<number, number[]>;

    constructor(urlLib: string[], edges: number[][]) {
        this.maps = new Map<string, number>();
        this.urls = urlLib;
        this.a = new Map<number, number[]>()
        for (let i = 0; i < urlLib.length; i++) {
            this.maps.set(urlLib[i], i)
        }

        for (let i = 0; i < edges.length; i++) {
            if(this.a.get(edges[i][0]) == null ){
                var t: number[] = [];
                t.push(edges[i][1]);
                this.a.set(edges[i][0],t) ;
            }
            else{
                var t = this.a.get(edges[i][0])!;
                t.push(edges[i][1]);
                this.a.set(edges[i][0],t);
            }
        }
    }

    getUrls(url: string): string[] {
        var ans: string[] = []
        if(this.maps.get(url) == undefined) return ans;
        var cur = this.maps.get(url)!;

        if(this.a.get(cur) == undefined) return ans;
        var urlList = this.a.get(cur)!;

        for(let i=0; i < urlList.length; i++){
            ans.push(this.urls[urlList[i]]);
        }

        return ans;
    }
};
